// Requires 'buffer' in Node.js or a Buffer polyfill in browsers

function ipv4ToBase32(ipStr) {
  // Parse the IP address
  const parts = ipStr.split('.');
  if (parts.length !== 4) {
    throw new Error('Invalid IPv4 address');
  }

  // Convert to bytes
  const bytes = new Uint8Array(4);
  for (let i = 0; i < 4; i++) {
    bytes[i] = parseInt(parts[i], 10);
  }

  // Encode to Base32
  const encoded = Buffer.from(bytes).toString('base32');

  // Replace padding with '8'
  return encoded.replace(/=/g, '8');
}

function ipv6ToBase32(ipStr) {
  // Parse the IPv6 address
  // This is a simplified version - in production, use a proper IPv6 parser
  const normalized = normalizeIPv6(ipStr);
  const bytes = new Uint8Array(16);

  // Convert to bytes (simplified)
  const groups = normalized.split(':');
  for (let i = 0; i < 8; i++) {
    const value = parseInt(groups[i], 16);
    bytes[i * 2] = (value >> 8) & 0xff;
    bytes[i * 2 + 1] = value & 0xff;
  }

  // Encode to Base32
  const encoded = Buffer.from(bytes).toString('base32');

  // Replace padding with '8'
  return encoded.replace(/=/g, '8');
}

function dualStackToBase32(ipv4Str, ipv6Str) {
  return ipv4ToBase32(ipv4Str) + ipv6ToBase32(ipv6Str);
}

// Helper function to normalize IPv6 address (simplified)
function normalizeIPv6(ipStr) {
  // This is a simplified version - in production, use a proper IPv6 parser
  let expanded = ipStr;

  // Handle :: compression
  if (expanded.includes('::')) {
    const parts = expanded.split('::');
    const left = parts[0] ? parts[0].split(':') : [];
    const right = parts[1] ? parts[1].split(':') : [];
    const missing = 8 - left.length - right.length;
    const middle = Array(missing).fill('0');
    expanded = [...left, ...middle, ...right].join(':');
  }

  // Ensure 8 groups
  const groups = expanded.split(':');
  return groups
    .map(g => g.padStart(4, '0'))
    .join(':');
}

// Examples
const ipv4 = "1.2.3.4";
const ipv6 = "2001:db8:85a3::8a2e:370:7334";

console.log(`IPv4 Base32: ${ipv4ToBase32(ipv4)}`);
console.log(`IPv6 Base32: ${ipv6ToBase32(ipv6)}`);
console.log(`Dual Stack Base32: ${dualStackToBase32(ipv4, ipv6)}`);

