package main

import (
	"encoding/base32"
	"fmt"
	"net"
	"strings"
)

// IPv4ToBase32 converts an IPv4 address to Base32 encoding
func IPv4ToBase32(ipStr string) (string, error) {
	// Parse the IP address
	ip := net.ParseIP(ipStr)
	if ip == nil {
		return "", fmt.Errorf("invalid IP address: %s", ipStr)
	}

	// Ensure it's an IPv4 address
	ipv4 := ip.To4()
	if ipv4 == nil {
		return "", fmt.Errorf("not an IPv4 address: %s", ipStr)
	}

	// Encode to Base32
	encoded := base32.StdEncoding.EncodeToString(ipv4)

	// Replace padding with '8'
	return strings.ReplaceAll(encoded, "=", "8"), nil
}

// IPv6ToBase32 converts an IPv6 address to Base32 encoding
func IPv6ToBase32(ipStr string) (string, error) {
	// Parse the IP address
	ip := net.ParseIP(ipStr)
	if ip == nil {
		return "", fmt.Errorf("invalid IP address: %s", ipStr)
	}

	// Ensure it's an IPv6 address
	ipv6 := ip.To16()
	if ipv6 == nil || ip.To4() != nil {
		return "", fmt.Errorf("not an IPv6 address: %s", ipStr)
	}

	// Encode to Base32
	encoded := base32.StdEncoding.EncodeToString(ipv6)

	// Replace padding with '8'
	return strings.ReplaceAll(encoded, "=", "8"), nil
}

// DualStackToBase32 creates a dual-stack Base32 encoding
func DualStackToBase32(ipv4Str, ipv6Str string) (string, error) {
	ipv4Encoded, err := IPv4ToBase32(ipv4Str)
	if err != nil {
		return "", err
	}

	ipv6Encoded, err := IPv6ToBase32(ipv6Str)
	if err != nil {
		return "", err
	}

	return ipv4Encoded + ipv6Encoded, nil
}

func main() {
	ipv4 := "1.2.3.4"
	ipv6 := "2001:0db8:85a3::8a2e:370:7334"

	ipv4Encoded, _ := IPv4ToBase32(ipv4)
	ipv6Encoded, _ := IPv6ToBase32(ipv6)
	dualStackEncoded, _ := DualStackToBase32(ipv4, ipv6)

	fmt.Printf("IPv4 Base32: %s\n", ipv4Encoded)
	fmt.Printf("IPv6 Base32: %s\n", ipv6Encoded)
	fmt.Printf("Dual Stack Base32: %s\n", dualStackEncoded)
}

