import java.net.InetAddress;
import java.net.UnknownHostException;
import java.util.Base64;

public class DNS2Base32 {

    public static String ipv4ToBase32(String ipStr) throws UnknownHostException {
        // Parse the IP address
        InetAddress ip = InetAddress.getByName(ipStr);

        // Ensure it's an IPv4 address
        if (!(ip instanceof java.net.Inet4Address)) {
            throw new IllegalArgumentException("Not an IPv4 address: " + ipStr);
        }

        // Get the raw bytes
        byte[] ipBytes = ip.getAddress();

        // Encode to Base32
        String encoded = Base64.getEncoder().encodeToString(ipBytes);

        // Replace padding with '8'
        return encoded.replace("=", "8");
    }

    public static String ipv6ToBase32(String ipStr) throws UnknownHostException {
        // Parse the IP address
        InetAddress ip = InetAddress.getByName(ipStr);

        // Ensure it's an IPv6 address
        if (!(ip instanceof java.net.Inet6Address)) {
            throw new IllegalArgumentException("Not an IPv6 address: " + ipStr);
        }

        // Get the raw bytes
        byte[] ipBytes = ip.getAddress();

        // Encode to Base32
        String encoded = Base64.getEncoder().encodeToString(ipBytes);

        // Replace padding with '8'
        return encoded.replace("=", "8");
    }

    public static String dualStackToBase32(String ipv4Str, String ipv6Str) throws UnknownHostException {
        return ipv4ToBase32(ipv4Str) + ipv6ToBase32(ipv6Str);
    }

    public static void main(String[] args) {
        try {
            String ipv4 = "1.2.3.4";
            String ipv6 = "2001:0db8:85a3::8a2e:370:7334";

            System.out.println("IPv4 Base32: " + ipv4ToBase32(ipv4));
            System.out.println("IPv6 Base32: " + ipv6ToBase32(ipv6));
            System.out.println("Dual Stack Base32: " + dualStackToBase32(ipv4, ipv6));
        } catch (UnknownHostException e) {
            e.printStackTrace();
        }
    }
}
