	"fmt"
	"net"
	"strings"
)

// IPv4ToBase32 å°†IPv4åœ°å€è½¬æ¢ä¸ºBase32ç¼–ç 
func IPv4ToBase32(ipStr string) (string, error) {
	// è§£æIPåœ°å€
	ip := net.ParseIP(ipStr)
	if ip == nil {
		return "", fmt.Errorf("æ— æ•ˆçš„IPåœ°å€: %s", ipStr)
	}

	// ç¡®ä¿æ˜¯IPv4åœ°å€
	ipv4 := ip.To4()
