// IPv6ToBase32 å°†IPv6åœ°å€è½¬æ¢ä¸ºBase32ç¼–ç 
func IPv6ToBase32(ipStr string) (string, error) {
	// è§£æIPåœ°å€
	ip := net.ParseIP(ipStr)
	if ip == nil {
		return "", fmt.Errorf("æ— æ•ˆçš„IPåœ°å€: %s", ipStr)
