}

// DualStackToBase32 åˆ›å»ºåŒæ ˆBase32ç¼–ç 
func DualStackToBase32(ipv4Str, ipv6Str string) (string, error) {
	ipv4Encoded, err := IPv4ToBase32(ipv4Str)
	if err != nil {
		return "", err
	}

	ipv6Encoded, err := IPv6ToBase32(ipv6Str)
